import React from 'react'
import '../../cssHelper/helper.css'
import './Categories.css'

function Categories(props) {

    const [accessToken, setAccessToken] = useState('')
    const [artists, setArtists] = useState([

    ])

    useEffect(() => {
        // to fetch the access token from spotify api 
        var authParameters = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: 'grant_type=client_credentials&client_id=' + clientId + '&client_secret=' + clientSecret
        }

        fetch(tokenEndPoint, authParameters)
            .then(result => result.json())
            .then(data => setAccessToken(data.access_token))
    }, [])

    useEffect(() => {
        // get artists 
        async function search() {
            const artistParameters = {
                method: 'get',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + accessToken,
                }
            }

            fetch(`${endPoint}search?q=${props.query}&type=artist`, artistParameters)
                .then(result => result.json())
                .then(data => setArtists(data.artists.items))

        }
        search()
    }, [{}])

    
    return (
        <div className='Categories w-full r-10 scroll-hidden relative' style={{ backgroundColor: props.bgClr }}>
            <div className="categories-title">
                {props.title}
            </div>
            <img src="https://th.bing.com/th/id/OIP.HTgSVB6PBCa6NOUQ3wybTgHaKI?rs=1&pid=ImgDetMain" alt="" className="absolute r-6 categories-img cover" />
        </div>
    )
}

export default Categories
