import '../../cssHelper/helper.css'
import './Artist.css'

function Artist() {

  const [accessToken, setAccessToken] = useState('')
    const [playlists, setPlaylists] = useState([])
    useEffect(() => {
        // to fetch the access token from spotify api 
        var authParameters = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: 'grant_type=client_credentials&client_id=' + clientId + '&client_secret=' + clientSecret
        }

        fetch(tokenEndPoint, authParameters)
            .then(result => result.json())
            .then(data => setAccessToken(data.access_token))
    }, [])

    useEffect(() => {
        async function searchLatest() {
            const artistParameters = {
                method: 'get',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + accessToken,
                }
            }

            fetch(`${endPoint}users/${shafiiqUserId}/playlists`, artistParameters)
                .then(result => result.json())
                .then(data => setPlaylists(data.items))
        }
        searchLatest()

        // console.log(playlists);

    }, [{}])

    
  return (
    <div className='Artist r-5 flex ease-4 pointer mt-1 flex-col'>
      <div className="artists-item w-full a-center flex gap-05">
      <img src="https://th.bing.com/th/id/R.2b1eef06401634f0851eed22a6ae6b53?rik=ZazbqETMH%2bE5mg&riu=http%3a%2f%2filarge.lisimg.com%2fimage%2f14785428%2f740full-natalia-andreeva.jpg&ehk=xT1t4iKCnkrpdqFWzsN4eQ1pXaqO46wnROdEBQoJtSk%3d&risl=&pid=ImgRaw&r=0" alt="" className="Artist-icon cover r-50" />
      <div className="flex flex-col j-center Artist-details">
        <span className='font-sm bold-400'>Moon Byul</span>
        <span>Artist</span>
      </div>
      </div>
    </div>
  )
}

export default Artist
