import React, { useEffect, useState } from 'react'
import '../../cssHelper/helper.css'
import './Hero.css'
import Recomentation from '../Recomentation/Recomentation'
import { clientId, clientSecret, endPoint, tokenEndPoint } from '../../Constants/constant'

function Hero() {

  const [accessToken, setAccessToken] = useState('')
  const [artists, setArtists] = useState([{}])

  useEffect(() => {
    // to fetch the access token from spotify api 
    var authParameters = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      },
      body: 'grant_type=client_credentials&client_id=' + clientId + '&client_secret=' + clientSecret
    }

    fetch(tokenEndPoint, authParameters)
      .then(result => result.json())
      .then(data => setAccessToken(data.access_token))
  }, [])

  useEffect(() => {
    // get artists 
    async function search() {
      const artistParameters = {
        method: 'get',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + accessToken,
        }
      }

      fetch(`${endPoint}search?q=Margaret Wiegel&type=artist`, artistParameters)
        .then(result => result.json())
        .then(data => console.log(data.artists.items))

    }
    search()
  }, [{}])


  function extractCounterItemsFromArtists(array, n) {
    return array.slice(0, n);
  }

  // Extract 4 items
  const limitedArtists = extractCounterItemsFromArtists(artists, 4);
  return (
    <div className='Hero primary-bg r-10 scroll-hidden'>
      <div className="Hero-scroll-wrapper w-full scroll-x scroll-y-hidden">
        <div className="hero-nav flex a-center j-start mt-2">
          <ul className="flex gap-05 a-center j-start white-clr bold-600">
            <li className="hero-li secondary-bg r-vh p-inline-1 flex a-center j-center pointer">
              All
            </li>
            <li className="hero-li secondary-bg r-vh p-inline-1 flex a-center j-center pointer">
              Music
            </li>
            <li className="hero-li secondary-bg r-vh p-inline-1 flex a-center j-center pointer">
              Podcasts
            </li>
          </ul>
        </div>
        <div className="frequent grid c-repeat-2 w-full mt-2 gap-05 p-inline-1">
          {
            limitedArtists.map((artist, index) => (
              <div key={index} className="frequent-items flex gap-05 a-center w-full r-5 white-clr bold-600 font-sm pointer">
                <img  alt={latest.name} className='cover frequent-img r-4' />
                <span>
                  {latearst.name}
                </span>
              </div>
            ))
          }
        </div>

        {/* recondamation section  */}
        <Recomentation query = 'Dua Lipa' heading='For Muhammed Shafeek'/>
        <Recomentation query = 'Dabzee' heading='Rap and Rock'/>
        <Recomentation query = 'Bilionera' heading='Bilionera'/>
        <Recomentation query = 'Taylor Swift' heading='Bilionera'/>
        <Recomentation query = 'SZA' heading='Bilionera'/>
        <Recomentation query = 'Sabrina Carpenter' heading='Recometation for You'/>
        <Recomentation query = 'The Marias' heading='English tops'/>
      </div>
    </div>
  )
}

export default Hero
