import React, { useEffect, useState } from 'react'
import '../../cssHelper/helper.css'
import './Recomentation.css'
import { clientId, clientSecret, endPoint, tokenEndPoint } from '../../Constants/constant'

function Recomentation() {

    const [accessToken, setAccessToken] = useState('')
    const [artists, setArtists] = useState([])

    useEffect(() => {
        // to fetch the access token from spotify api 
        var authParameters = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: 'grant_type=client_credentials&client_id=' + clientId + '&client_secret=' + clientSecret
        }

        fetch(tokenEndPoint, authParameters)
            .then(result => result.json())
            .then(data => setAccessToken(data.access_token))
    }, [])

    // get artists 
    async function search() {
        const artistParameters = {
            method: 'get',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + accessToken,
            }
        }

        fetch(`${endPoint}search?q=Dua Lipa&type=artist`, artistParameters)
            .then(result => result.json())
            .then(data => setArtists(data.artists.items[0], data.artists.items[1], data.artists.items[2], data.artists.items[3]))
    }
    search()


    return (
        <div className='Recometation flex flex-col gap-1 white-clr mt-2 p-inline-1'>
            <div className="recomentation-head flex j-between a-center">
                <h1 className='recomention-heading bold-600'>
                    Made For Muhammed Shafeek
                </h1>
                <span className="show-all bold-500 pointer">
                    Show all
                </span>
            </div>
            <div className="recomentation-body grid c-repeat-4 w-full">
                {
                    artists.map((artist) => {
                        
                    })
                }
            </div>
        </div>
    )
}

export default Recomentation
