import React, { useEffect, useState } from 'react'
import '../../cssHelper/helper.css'
import './Categories.css'
import { clientId, clientSecret, endPoint, tokenEndPoint } from '../../Constants/constant'

function Categories(props) {
    const [accessToken, setAccessToken] = useState('')
    // const [artists, setArtists] = useState([])

    useEffect(() => {
        // to fetch the access token from spotify api 
        var authParameters = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: 'grant_type=client_credentials&client_id=' + clientId + '&client_secret=' + clientSecret
        }

        fetch(tokenEndPoint, authParameters)
            .then(result => result.json())
            .then(data => setAccessToken(data.access_token))
    }, [])

    useEffect(() => {
        // get artists 
        async function searchCategories() {
            const artistParameters = {
                method: 'get',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + accessToken,
                }
            }

            fetch(`${endPoint}browse/categories`, artistParameters)
                .then(result => result.json())
                .then(data => console.log(data))

        }
        searchCategories()
    }, [{}])


    return (
        
        <div className='Categories grid c-repeat-3 gap-1 w-full mt-2 w-full ' style={{ backgroundColor: props.bgClr }}>
            <div className='r-10 scroll-hidden relative'>
            <div className="categories-title">
                {props.title}
            </div>
            <img src="https://th.bing.com/th/id/OIP.HTgSVB6PBCa6NOUQ3wybTgHaKI?rs=1&pid=ImgDetMain" alt="" className="absolute r-6 categories-img cover" />

            </div>
        </div>
    )
}

export default Categories
