import React, { useContext } from 'react';
import Sidebar from '../Sidebar/Sidebar';
import '../../cssHelper/helper.css';
import './Section.css';
import Hero from '../Hero/Hero';
import Related from '../Related/Related';
import SerachResults from '../SearchResult/SerachResults';
import { SearchIsClicked, IsClicked } from '../../context/SearchContext';
import SearchEngine from '../SearchEngine/SearchEngine'; // Make sure the import path is correct

function Section() {
  return (
    <IsClicked>
      <SectionContent />
    </IsClicked>
  );
}

function SectionContent() {
  const { isSearch } = useContext(SearchContext);

  return (
    <div className='Section flex'>
      <Sidebar />
      {isSearch ? <SerachResults /> : <Hero />}
      <Related />
    </div>
  );
}

export default Section;
