import React, { useEffect, useState } from 'react'
import '../../cssHelper/helper.css'
import './Categories.css'
import { clientId, clientSecret, endPoint, tokenEndPoint } from '../../Constants/constant'

function Categories(props) {
    const [accessToken, setAccessToken] = useState('')
    const [categories, setCategories] = useState([])

    useEffect(() => {
        // to fetch the access token from spotify api 
        var authParameters = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: 'grant_type=client_credentials&client_id=' + clientId + '&client_secret=' + clientSecret
        }

        fetch(tokenEndPoint, authParameters)
            .then(result => result.json())
            .then(data => setAccessToken(data.access_token))
    }, [])

    useEffect(() => {
        // get artists 
        async function searchCategories() {
            const artistParameters = {
                method: 'get',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + accessToken,
                }
            }

            fetch(`${endPoint}browse/categories`, artistParameters)
                .then(result => result.json())
                .then(data => console.log(data.categories.items))

        }
        searchCategories()
    }, [{}])

    const print = () => {
        console.log(categories);
    }


    return (

        <div onClick={print} className='Categories grid c-repeat-3 gap-1 mt-2 w-full pr-1'>
            {
                
            }
        </div>
    )
}

export default Categories
