import React, { useEffect, useState } from 'react'
import '../../cssHelper/helper.css'
import './Library.css'
import { clientId, clientSecret, endPoint, tokenEndPoint } from '../../Constants/constant'
import axios from '../../Axios/axios'

function Library() {
    // store that access token 
    const [accessToken, setAccessToken] = useState('')
    const [playlists, setPlaylists] = useState([])
    useEffect(() => {
        // to fetch the access token from spotify api 
        var authParameters = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: 'grant_type=client_credentials&client_id=' + clientId + '&client_secret=' + clientSecret
        }

        fetch(tokenEndPoint, authParameters)
            .then(result => result.json())
            .then(data => setAccessToken(data.access_token))
    }, [])

    useEffect(() => {
        async function searchLatest() {
            const artistParameters = {
                method: 'get',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + accessToken,
                }
            }

            fetch(`${endPoint}users/31q2bv54j4iegidrgzazvkewj3i4/playlists`, artistParameters)
                .then(result => result.json())
                .then(data => console.log(data.items))
        }
        searchLatest()

    }, [])


    return (
        <div className='Library r-5 flex ease-4 pointer'>
            <img src="https://th.bing.com/th/id/OIP.TJ66IpomLzIaSMmXty5EFQHaJQ?rs=1&pid=ImgDetMain" alt="" className="library-icon cover r-5" />
            <div className="flex flex-col j-center library-details">
                <span className='font-sm bold-400'>Liked Songs</span>
                <span className='flex' style={{ gap: '5px' }}>
                    <span>Playlist</span>
                    <span>.</span>
                    <span>48</span>
                    <span>songs</span>
                </span>
            </div>
        </div>
    )
}

export default Library
